class Database{
    public $conn;
    
public function __construct()
{
    $this->connect_db();
}
    public function connect_db(){
        $this->conn=mysqli_connect("localhost","root","","estore");
if(mysqli_connect_error()){
    die("cannot conect to server". mysqli_connect_error() . mysqli_connect_errno());
        }
    }
}
$database = new Database();
$database->connect_db();

 $emailErr="";

function test_input($data) {
  $data = trim($data);
  $data = stripslashes($data);
  $data = htmlspecialchars($data);
  return $data;
}


if(isset($_POST['login'])){

if (empty($_POST["email"])) {
    $emailErr = "Email is required";
  } else {
    $email = test_input($_POST["email"]);
    // check if e-mail address is well-formed
    if (!filter_var($email, FILTER_VALIDATE_EMAIL)) {
      $emailErr = "<div class='alert alert-danger' role='alert'>
     Invalid email format
    </div>";
    }else{
  
    $pass=md5($_POST['password']);

      $query="SELECT * FROM vendor WHERE vendor_email='$email' AND vendor_pass='$pass' ";
    $result=mysqli_query($database->conn,$query);
    $admin=mysqli_fetch_assoc($result);
    if(!empty($admin['vendor_id'])){
        $_SESSION['idvendor']=$admin['vendor_id'];
header("location:indexv.php");
    }else{
        $error= "user not found";
    }
  }
  }
}

